                                }
                                else
                                {

                                    htmlElement.___style.WhiteSpace = "nowrap";
                                    //htmlElement.___style.Display = "block";
                                    //htmlElement.SetChildrenDisplayStyle("inline", true, true);

                                }
                                continue;
                            case "face":
