										}
                                        catch (Exception ex)
                                        {
                                            if (commonLog.LoggingEnabled && commonLog.CommonLogLevel >= 10)
                                            {
                                                commonLog.LogEntry("{0} has error {1}", this, commonData.GetExceptionAsString(ex));
                                            }
