                    case (int)CHtmlEventOriginatorType.CHTMLWindow:
                        {
                            if (___function == null)
                            {
                                CHtmlAttribute attrRemove = null;
                                if (this.___attributesForWindow.TryGetValue(__evtName, out attrRemove) == true)
